import React, { Component } from "react";
import Card from "./card";
import { FaReact } from "react-icons/fa";
import { FaSass } from "react-icons/fa";

class Cards extends Component {
  render() {
    return (
      <div className="container-fluid d-flex justify-content-center">
        <div class="row">
          <div className="col-md-4">
            <Card
              favi={<FaReact />}
              title="React"
              cardContent="I have recently set out to learn React and its libraries. I have found React to be similar to php in a way that makes builds easily scalable and easy to update. It truly showcases the well known DRY saying used amongst programmers."
            />
          </div>
          <div className="col-md-4">
            <Card
              favi={<FaSass />}
              title="Sass/CSS"
              cardContent="CSS alone has always seemed very repetive to me, yet the solution of using constants seemed so obvious. It wasn't until I set aside some time to learn Sass and how a styling preproccesser could benefit me so much in my development time."
            />
          </div>
          <div className="col-md-4">
            <Card />
          </div>
        </div>
      </div>
    );
  }
}

export default Cards;
