import React, { Component } from "react";

class Form extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: ""
    };
  }

  handleUsernameChange = e => {
    e.preventdefault();
    this.setState({
      username: e.target.value
    });
  };

  render() {
    return (
      <form>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={this.state.username}
            onChange={this.handleUsernameChange}
          />
          >
        </div>
      </form>
    );
  }
}

export default Form;
